//program to swap two number without using temprory veriable
package edu;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int a,b;
	a=20;
	b=30;
	System.out.println("Before interchanging a="+a+ "b="+b);
	a=a+b;
	b=a-b;
	a=a-b;
	System.out.println("After interchanginga="+a+"b="+b);

	}

}
